function sortByPositionNumber(arr) {
    return arr.sort((a, b) => +a.position - +b.position);
}

msg.menu = sortByPositionNumber(msg.payload.menu).map(el => {

    if (el.isParent) {
        return {
            "label": el.name,
            "value": el.name,
            "icon": el.icon ? el.icon : "",
            "children": el.childModules.length 
                ? sortByPositionNumber(el.childModules)
                  .map(item => ({
                      "label": item.name,
                      "value": item.name,
                      "icon": item.icon ? item.icon : ""
                  })) 
                : []
        }
    } else {
        return {
            "label": el.name,
            "value": el.name,
            "children": [],
            "icon": el.icon ? el.icon : "",
        }
    }

});

return msg;

const pageName = msg.check.page_id.replace('%20', ' ');

const parentPage = msg.menu.map((el) => el.label).includes(pageName);
const childPage = msg.menu.filter((e) => e.children.length > 0).flatMap((el) => el.children).map((elem) => elem.label).includes(pageName);

if (parentPage || childPage || pageName == "Chat" || pageName == "Vectors" || pageName == "Content" || pageName === "BaseDetails") {

    msg.statusCode = 200;
    msg.payload = {
        auth: "Success",
        code: 200,
        user: {
            "menu": msg.menu,
            "permission": msg.permissions,
            "translation": msg.payload.translation[0],
            "info": msg.user
        }
    }

} else {

    msg.payload = {
        auth: "Unautorizate",
        code: 401,
        login_id: msg.menu[0].value + `?pageName=${msg.menu[0].value}`,
        app_id: msg.check.app_id
    }

}
return msg;

