function transformData(data) {
    const result = [];

    data.forEach(item => {
        if (item.type === 'telegram') {
            result.push({
                type: item.user_name,
                message: item.requestText,
                date: moment(item.startDate).format('LLL')
            });

            result.push({
                type: 'BOT',
                message: item.answer,
                date: moment(item.endDate).format('LLL')
            });
        }
    });

    return result;
}

