const typeBase = msg.UNIFIED_DATA.DATABASE.TYPE_BASE;

switch (true) {
    case (typeBase === "pinecone" &&
        msg.UNIFIED_DATA.PINECONE.API_KEY !== null // in case we lost data in global
    ): {
            msg.type = 'pinecone';

            return [msg, null, null];
        }

    case (typeBase === "chroma"): {
        msg.type = 'chroma';

        return [null, msg, null];
    }

    case (typeBase === null): {
            msg.type = 'onlyGPT';

            return [null, null, msg, null];
        }

    case (msg.statusCode > 201 || msg.payload === "Enter OPENAI_API_KEY"): {
        msg.type = 'custom message';

        return [null, null, null, msg];
    }

    default:
        msg.payload = '⚙️ Please, check all settings including openAI key and Knowledge base. ⚙️';
        msg.type = 'custom message';

        return [null, null, null, msg];
}

