let responseChatGPT;

// Перевірка, чи статус-код менший за 201 або вказаний API ключ неправильний
if (msg.statusCode < 201 || msg.payload !== "Enter OPENAI_API_KEY") {

    // Встановлення значення відповіді з першого повідомлення від OpenAI API (якщо воно існує)
    responseChatGPT = (msg.type !== 'custom message') ? msg.payload?.choices[0]?.message?.content : msg.payload;
} else {

    // Отримання повідомлення про помилку або вивід статус-коду та відповіді
    const errorMessage = typeof msg.payload === 'string'
        ? msg.payload
        : msg.payload?.error.message;

    // Встановлення значення відповіді з глобального повідомлення (якщо воно існує), або виведення деталей помилки
    responseChatGPT = msg.UNIFIED_DATA.PREPARED_MESSAGES
        ? msg.UNIFIED_DATA.PREPARED_MESSAGES.ERROR
        : `statusCode: ${msg.statusCode} \npayload: ${errorMessage}`;
}

let history = global.get(`history${msg.UNIFIED_DATA.chat.chatId}`) || [];

