    .join(', ');

  msg.payload = {
    model: msg.model,
    messages: msg.messages = [
    { "role": "system", "content": `${totalPrompt}. ${vectorsForPrompt}`},
      ...msg.fromApp.messages
  ]}
}

return msg;

msg.fromApp = msg.payload


msg.collection = 'knowlegebase'

msg.payload = { systemName: msg.fromApp.baseID } 
return msg;

msg.behaviour = msg.payload[0].behaviour
msg.type = msg.payload[0].type
msg.payload = {
    ownerId: msg.payload[0].ownerID,
}
msg.collection = 'credantials'
return msg;

msg.buffer = msg.payload.img.data
msg.payload = msg.buffer;
return msg;

