function countEmptyAndFilledFields(obj) {
    const result = {};

    for (const key in obj) {
        const subObj = obj[key];
        const totalFields = Object.keys(subObj).length;
        const filledFieldsForKey = Object.values(subObj).filter(value => !!value).length;

        const percentage = filledFieldsForKey === 0 ? 0 : Math.round((filledFieldsForKey / totalFields) * 100);

        result[key] = {
            filledFields: filledFieldsForKey,
            totalFields: 1,
            percentage: percentage
        };
    }

    const totalFields = Object.values(result).reduce((acc, curr) => acc + curr.totalFields, 0);
    const filledFields = Object.values(result).reduce((acc, curr) => acc + curr.filledFields, 0);
    const totalPercentage = filledFields === 0 ? 0 : Math.round((filledFields / totalFields) * 100);

    result["total"] = {
        filledFields: filledFields,
        totalFields: totalFields,
        percentage: totalPercentage
    };

    return result;
}

